@st.cache(allow_output_mutation=True)
def Load_ml_items(relative_path):
    "Load ML items to reuse them"
    with open(relative_path, 'rb') as file:
        loaded_items = pickle.load(file)
    return loaded_items

loaded_items=Load_ml_items(r'ml_comp.pkl')


